import React, { useEffect, useRef } from 'react'
import { View, TextInput, TouchableOpacity, Text } from "./components/Themed"
import { StyleSheet, TextInput as HiddenText } from 'react-native'

let mockdata : Array<String> = [
    "Harry Potter and the Half-Blood Prince",
    "Daniel Radcliffe",
    "Rupert Grint",
    "Emma Watson",
    "Helena Bonham Carter",
    "Robbie Coltrane",
    "Warwick Davis"
]



export default function Puzzle () {
    function keyHandler(key: string){
        console.log(key)
    }

    const inputRef = useRef(null);
    useEffect(() => {
        inputRef.current && inputRef.current.focus();
    }, []);

    return <View>
        <HiddenText ref={inputRef} style={{width: 0, height: 0}} onChange={(e) => {
            keyHandler(e.target.value);
            e.target.value = ""
        }}></HiddenText>
        <View style={styles.text}></View>
        <TouchableOpacity onPress={() => keyHandler(1)}>
            <Text>Q</Text>
        </TouchableOpacity>
        <TouchableOpacity onPress={() => keyHandler(2)}>
            <Text>W</Text>
        </TouchableOpacity>
        <TouchableOpacity onPress={() => keyHandler(3)}>
            <Text>E</Text>
        </TouchableOpacity>
    </View>
}

const styles = StyleSheet.create({
    text: {
        margin: 5,
    },
    container: {
        alignContent: 'center'
    }
})
